import { useState } from 'react';
import useInput from '../hooks/use-input';

const BasicForm = (props) => {
  const {
    value: enteredFirstname,
    isValid: firstNameIsValid,
    hasError: firstNameHasError,
    valueChangeHandler: firstNameChangeHandler,
    valueBlurHandler: firstNameBlurHandler,
    reset: resetFirstname
  } = useInput(value => value.trim() !== '');
  // const [enteredFirstname, setEnteredFirstname] = useState('');
  // const [enteredFirstnameTouched, setEnteredFirstnameTouched] = useState(false);
  // const firstNameIsInvalid = enteredFirstname.trim() === '';
  // const firstNameHasError = firstNameIsInvalid && enteredFirstnameTouched;

  let formIsValid = false;

  if (firstNameIsValid) {
    formIsValid = true;
  }

  // const firstNameChangeHandler = event => {
  //   setEnteredFirstname(event.target.value);
  // }

  // const firstNameBlurHandler = () => {
  //   setEnteredFirstnameTouched(true);
  // }

  const formSubmissionHandler = event => {
    event.preventDefault();

    // setEnteredFirstnameTouched(true)
    if (!firstNameIsValid) {
      return;
    }
    console.log(enteredFirstname);
    // setEnteredFirstname('');
    // setEnteredFirstnameTouched(false);
    resetFirstname();
  }

  const firstNameInputClasses = firstNameHasError ? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className='control-group'>
        <div className={firstNameInputClasses}>
          <label htmlFor='name'>First Name</label>
          <input
            type='text'
            id='name'
            value={enteredFirstname}
            onChange={firstNameChangeHandler}
            onBlur={firstNameBlurHandler}
          />
          {firstNameHasError && <p>Name must be filled.</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
